import React, { useState, useEffect, useCallback } from 'react';

// Helper function to debounce events
const debounce = (func, delay) => {
  let timer;
  return function (...args) {
    const context = this;
    clearTimeout(timer);
    timer = setTimeout(() => func.apply(context, args), delay);
  };
};

// Simplified App component
const App = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  const loadItems = useCallback(async () => {
    if (loading || !hasMore) return;
    setLoading(true);

    try {
      // Adjust the API endpoint to include pagination, if necessary
      const response = await fetch(`https://639c50f416d1763ab1461101.mockapi.io/usedata?page=${page}`);
      const data = await response.json();
      console.log(data);

      if (data.length === 0) {
        setHasMore(false);
      } else {
        setItems((prevItems) => [...prevItems, ...data]);
        setPage((prevPage) => prevPage + 1);
      }
    } catch (error) {
      console.error('Failed to fetch items:', error);
    } finally {
      setLoading(false);
    }
  }, [loading, hasMore, page]);

  useEffect(() => {
    // Debounced scroll handler
    const handleScroll = debounce(() => {
      if (loading || !hasMore) return;

      const scrollPosition = window.innerHeight + document.documentElement.scrollTop;
      const isBottom = scrollPosition >= document.documentElement.offsetHeight - 1; // Use a threshold to account for rounding

      if (isBottom) {
        loadItems();
      }
    }, 200); // 200ms debounce delay

    window.addEventListener('scroll', handleScroll);
    loadItems(); // Load initial items

    return () => window.removeEventListener('scroll', handleScroll);
  }, [loadItems, loading, hasMore]);

  return (
    <div>
      <ul>
        {items.map((item, index) => (
          <li key={index}>{item.name}</li>
        ))}
      </ul>
      {loading && <p>Loading...</p>}
      {!hasMore && <p>No more items</p>}
    </div>
  );
};

export default App;
