import React, { useState, useEffect } from 'react';
import './App.css';
import axios from "axios";

function App() {
  const [data, setData] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const limit = 10;

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const res = await axios.get(`https://639c50f416d1763ab1461101.mockapi.io/usedata?limit=${limit}&page=${page}`);
        setData((prevData) => [...prevData, ...res.data]); // Append new data
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data", error);
        setLoading(false);
      }
    };

    fetchData();
  }, [page]);

  const handleInfinitescroll = async () => {
    try {
      if (window.innerHeight + document.documentElement.scrollTop >= document.documentElement.scrollHeight) {
        setPage((prev) => prev + 1);
      }
    } catch (error) {
      console.error("Error handling infinite scroll", error);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleInfinitescroll);

    return () => {
      window.removeEventListener("scroll", handleInfinitescroll);
    };
  }, []);

  console.log(data);

  return (
    <div className="App">
      {data.map((item) => (
        <div key={item.id} className="product-card">
          <img src={item.image} alt={item.title} className="product-image" style={{ width: "100px" }} />
          <p className="product-price">{item.id}</p>
          <h3 className="product-title">{item.title}</h3>
        </div>
      ))}
      {loading && <p>Loading more items...</p>}
    </div>
  );
}

export default App;
