import React,{useEffect, useState} from 'react';
import './App.css';

function App() {
  const[theme,setTheme]=useState(false)

  useEffect(()=>{
    setTheme(true)
  },[])

  const handleClick=()=>{
    setTheme(!theme)
  }
  return (
    <div className="App">
      <button>Toggle me</button>
    </div>
  );
}

export default App;
