import React, { useState } from 'react'
import TodoList from './TodoList'

const Todo = () => {
  const[data,setData]=useState("")
  const[todos,setTodos]=useState([])
   const handleClick=()=>{
    setTodos([...todos,data])
    setData("")
   }

   
  return (
    <div>
        <h1>Todo List</h1>
        <input type="text" placeholder="Enter a task" value={data} onChange={(e)=>setData(e.target.value)}/>

        <button onClick={handleClick}>Add</button>
        <div>
          <button>Edit</button>
        </div>

      <TodoList todos={todos}/>
    </div>
  )
}

export default Todo
